import React from 'react';
import api from '../../services/api';

const KanbanCard = ({ kanban, dashboardType, setKanbans, productID }) => {

    const handleStatusChange = async () => {
        console.log("KanbanCard - handleStatusChange CALLED - kanban_id:", kanban.kanban_id, "productID:", productID); // Log at start
        console.log("KanbanCard - handleStatusChange - setKanbans prop value BEFORE API CALL:", setKanbans); // Log setKanbans prop BEFORE API call

        try {
            console.log("KanbanCard - handleStatusChange: Making API call...");
            const response = await api.put(`/kanbans/${kanban.kanban_id}`, { status_current: parseInt(kanban.status_current, 10) });
            console.log("KanbanCard - handleStatusChange: API response received:", response.data);

            console.log("KanbanCard - handleStatusChange - setKanbans prop value BEFORE setKanbans CALL:", setKanbans); // Log setKanbans prop BEFORE setKanbans call

            if (dashboardType === "customer") { 
                setKanbans(prevKanbansByProduct => {
                    console.log("KanbanCard - setKanbans (customer) CALLBACK - productID prop value (INSIDE CALLBACK):", productID); // Log productID INSIDE callback
                    console.log("KanbanCard - setKanbans (customer) CALLBACK - setKanbans prop value (INSIDE CALLBACK):", setKanbans); // Log setKanbans prop INSIDE callback - VERY IMPORTANT!

                    return prevKanbansByProduct; // SIMPLIFIED - NO STATE UPDATE LOGIC YET
                }, () => {
                    console.log("KanbanCard - setKanbans (customer) CALLBACK EXECUTED");
                });
            }

        } catch (error) {
            console.error("KanbanCard - Error updating Kanban status", error);
        }
    };

    return (
        <div className="kanban-card" style={{ borderColor: kanban.status_color, borderWidth: '3px' }}>
            <h2>{kanban.status_name}</h2>
            <p><strong>Product:</strong> {kanban.product_name} (ID: {productID})</p> 
            <p><strong>Container:</strong> {kanban.tipo_contenitore}</p>
            <p><strong>Quantity:</strong> {kanban.quantity}</p>
            <button onClick={handleStatusChange}>
                Change Status (ProductID: {productID})
            </button>
        </div>
    );
};

export default KanbanCard;
