import React from 'react';
import api from '../../services/api';

const KanbanCard = ({ kanban, dashboardType, setKanbans, productID }) => {

    const handleStatusChange = async () => {
        console.log("KanbanCard - handleStatusChange CALLED - kanban_id:", kanban.kanban_id, "productID:", productID); // Log at start
        try {
            console.log("KanbanCard - handleStatusChange: Making API call...");
            const response = await api.put(`/kanbans/${kanban.kanban_id}`, { status_current: parseInt(kanban.status_current, 10) });
            console.log("KanbanCard - handleStatusChange: API response received:", response.data);

            if (dashboardType === "customer") { // FOR TESTING - ONLY CUSTOMER DASHBOARD FOR NOW
                setKanbans(prevKanbansByProduct => {
                    console.log("KanbanCard - setKanbans (customer) CALLED - productID (inside callback):", productID); // Log productID INSIDE callback

                    return prevKanbansByProduct; // **SIMPLIFIED - NO STATE UPDATE LOGIC YET - JUST RETURN PREVIOUS STATE**
                }, () => {
                    console.log("KanbanCard - setKanbans (customer) CALLBACK EXECUTED");
                });
            }

        } catch (error) {
            console.error("KanbanCard - Error updating Kanban status", error);
        }
    };

    return (
        <div className="kanban-card" style={{ borderColor: kanban.status_color, borderWidth: '3px' }}>
            <h2>{kanban.status_name}</h2>
            <p><strong>Product:</strong> {kanban.product_name} (ID: {productID})</p> {/* Display productID in UI */}
            <p><strong>Container:</strong> {kanban.tipo_contenitore}</p>
            <p><strong>Quantity:</strong> {kanban.quantity}</p>
            <button onClick={handleStatusChange}> {/* Button ALWAYS VISIBLE FOR TESTING */}
                Change Status (ProductID: {productID}) {/* Display productID on button */}
            </button>
        </div>
    );
};

export default KanbanCard;
